import { StatusBar } from 'expo-status-bar'
import React, {useState, useEffect} from 'react'
import { StyleSheet, Text, View, Button, TouchableOpacity } from 'react-native'
import moment from 'moment'
import "./styles.css"
import buildCalendar from "./build"

export default function Calendar() {
    const [calendar,setCalendar] = useState([])
    const [value, setValue] = useState(moment())


    useEffect(() => {
        setCalendar(buildCalendar(value))
    }, [value])

    function isSelected(day) {return value.isSame(day, "day");}
    function beforeToday(day){return day.isBefore(new Date(), "day ")}
    function isToday(day) {return day.isSame(new Date(), "day");}
    function dayStyles(day){
        if(beforeToday()) {return "before"}
        if(isSelected()) {return "selected"}
        if(isToday()) {return "today"}
        return ""
    }

    return( 

    <View>
        {calendar.map((week) => (
            <View style={styles.week}>
              {week.map((day) => (
              <TouchableOpacity
              onPress={()=>{setValue(day)}}
              style={[ isSelected() ? styles.selected : styles.days]}>
              <Text>{day.format("D").toString()}</Text>
              </TouchableOpacity>
            ))}
            </View>
        ))}
    </View>
    )
}

const styles = StyleSheet.create({
    week: {
        flexDirection: "row",
        textAlign: 'center',

    },

    selected: {
        backgroundColor: 'red',
    },

    days: {
        width: 40,
        height: 40,
        margin: 30,
        flexWrap: 'wrap',
        display: 'flex',
        position: 'relative',
    },
    text: {
        fontSize: 16,
        justifyContent: "center",
    }
})
